={{ 
  let score = 50;

  // Revenue scoring
  if ($json.quizAnswers.revenue === '2m-5m') score += 20;
  if ($json.quizAnswers.revenue === '5m-10m') score += 25;
  if ($json.quizAnswers.revenue === 'over-10m') score += 30;
  if ($json.quizAnswers.revenue && $json.quizAnswers.revenue.startsWith('other:')) score += 15;

  // Tax approach scoring
  if ($json.quizAnswers.taxApproach === 'once-year') score += 30;
  if ($json.quizAnswers.taxApproach === 'self-done') score += 35;
  if ($json.quizAnswers.taxApproach === 'quarterly-reactive') score += 15;
  if ($json.quizAnswers.taxApproach && $json.quizAnswers.taxApproach.startsWith('other:')) score += 25;

  // Industry scoring
  if ($json.quizAnswers.industry === 'medical') score += 15;
  if ($json.quizAnswers.industry === 'production') score += 20;
  if ($json.quizAnswers.industry === 'services') score += 10;

  return Math.min(score, 100);
}}

={{ 
  let score = 50;

